import React, { Component } from 'react';
import './App.css';
import Button from './Button.js'
import Staff from './images/staff.png';
import Treble from './images/treble.png';
import T44 from './images/44.png';
import {Link} from 'react-router-dom'

class App extends Component {
  constructor() {
    super();
    this.state = {mx: 0, my: 0, x: 0, y: 0, nx: -100, ny: -100,
		  note: "quarter", speed: "100",
		  notes: [undefined,
			  {note:"treble",x:1,y:22}, undefined,
			  {note:"44",x:3,y:22}]
		  };
  }
  setSelectedNote = (selected) => {
    this.setState((prevState, props) => {
	return { note: prevState.note = selected }
    });
  }
  getNoteX = (x) => {
    if (x < 1 || x > 59) return -100
    return Math.round((x * 12 + 30) * window.innerWidth / 1000)
  }
  getNoteY = (y) => {
    return Math.round((y * 6.6 - 47) * window.innerWidth / 1000 + 100)
  }
  rerender() {
    this.forceUpdate();
  }
  componentDidMount() {
    window.addEventListener("resize", this.rerender.bind(this));
  }
  componentWillUnmount() {
    window.removeEventListener("resize", this.rerender.bind(this));
  }
  _onMouseMove(e) {
    this.setState({ mx: Math.round(e.pageX / window.innerWidth * 1000),
		    my: Math.round((e.pageY - 70) / window.innerWidth * 1000),
		    x: Math.round((this.state.mx - 47) / 12),
		    y: (this.state.note[0] === "r" ? 23 :
			!isNaN(parseInt(this.state.note[0])) ||
			this.state.note === "treble" ||
			this.state.note === "bass" ? 22 :
			Math.round((this.state.my) / 6.6)),
		    nx: (this.state.my < 20 || this.state.my > 220) ? -100 :
			this.getNoteX(this.state.x),
		    ny: (this.state.my < 20 || this.state.my > 220) ? -100 :
			this.getNoteY(this.state.y)
		})
    /*
    if (!isNaN(parseInt(this.state.note[0]))) {
      this.setState({x: Math.round((this.state.x / 15) * 15 + 5),
		     nx: (this.state.my < 20 || this.state.my > 220) ? -100 :
                        this.getNoteX(this.state.x)
		   })
      console.log(this.state.x)
    }*/
  }
  _onMouseDown(e) {
    if (this.state.nx < 0 || this.state.ny < 0) return
    if (this.state.x < 1 || this.state.x > 59 || this.state.y < 2 || this.state.y > 29) return
    let curNote = this.state.notes[this.state.x]
    if (this.state.note === "x") {
      this.state.notes[this.state.x] = undefined
    }
    else if (curNote === undefined || !(curNote.y === this.state.y &&
				   curNote.note === this.state.note)) {
      this.state.notes[this.state.x] =
  	//{note:this.state.note, pos:[this.state.nx, this.state.ny]}
	  {note:this.state.note, x:this.state.x, y:this.state.y}
    }
    else {
      this.state.notes[this.state.x] = undefined
    }
    this.forceUpdate()
  }
  renderNotes() {
    let notes = []
    for (let i = 1; i < this.state.notes.length; i++) {
      let note = this.state.notes[i]
      if (note != undefined) notes.push(<img id="note"
            src={require("./images/" + note.note + ".png")} alt=""
            style={{left: this.getNoteX(note.x) + "px",
		    top: this.getNoteY(note.y) + "px"}}/>)
    }
    return notes
  }
  render() {
    return (
      <div className="App" onMouseMove={this._onMouseMove.bind(this)}
	onMouseDown={this._onMouseDown.bind(this)}>
	<div id="body">
	  <img id="staff" src={Staff} />
	  <img id="indicator"
	    src={require("./images/" + this.state.note + ".png")} alt=""
	    style={{left: this.state.nx + "px", top: this.state.ny + "px",
		    visibility: (this.state.note === "x" ? "hidden" : "visible")}}/>
	  {this.renderNotes()}
	  <div>
	    <form id="playback">
	      <button id="playbutton" type="button">Play &#x25ba;</button>
	      <p>Speed (bpm):</p>
	      <div>
		<button className="sbutton" id="speed-" type="button">-</button>
	        <input id="speedinput" type="text" name="speed" defaultValue="100" />
		<button className="sbutton" id="speed+" type="button">+</button>
		<br/>
	      </div>
	    </form>
	    <div id="notes">
	      <Button image="quarter" selected={this.state.note}
		callback={this.setSelectedNote}></Button>
              <Button image="eighth" selected={this.state.note}
		callback={this.setSelectedNote}></Button>
              <Button image="half" selected={this.state.note}
		callback={this.setSelectedNote}></Button>
	      <Button image="eighthbar" selected={this.state.note}
                callback={this.setSelectedNote}></Button>
	      <Button image="quarterdot" selected={this.state.note}
                callback={this.setSelectedNote}></Button>
	      <Button image="x" selected={this.state.note}
                callback={this.setSelectedNote}></Button>

	      <Button image="rquarter" selected={this.state.note}
                callback={this.setSelectedNote}></Button>
	      <Button image="reighth" selected={this.state.note}
                callback={this.setSelectedNote}></Button>
              <Button image="rhalf" selected={this.state.note}
                callback={this.setSelectedNote}></Button>
              <Button image="flat" selected={this.state.note}
                callback={this.setSelectedNote}></Button>
              <Button image="sharp" selected={this.state.note}
                callback={this.setSelectedNote}></Button>
	      <Button image="natural" selected={this.state.note}
                callback={this.setSelectedNote}></Button>
	    </div>
	    <div id="select">
              <Button image="treble" selected={this.state.note}
                callback={this.setSelectedNote} size="8"></Button>
              <Button image="34" selected={this.state.note}
                callback={this.setSelectedNote} size="8"></Button>
	      <Link to="/changeclef">
	        <button className="cbutton" type="button">Change</button>
	      </Link>
	      <Link to="/changetime">
	        <button className="cbutton" type="button">Change</button>
	      </Link>
            </div>
	  </div>
	</div>
      </div>
    );
  }
}

export default App;
