import React, { Component } from 'react';
import './App.css'

class Button extends Component {
  render() {
    return (
      <button type="button" className="nbutton" onClick={() => {this.props.callback(this.props.image)}}
	style={{backgroundColor: (this.props.selected === this.props.image ? "skyblue" : "lightgrey"),
		backgroundImage: "url(" + require("./images/" + this.props.image + ".png") + ")",
		width: (this.props.size === undefined ? "4vw" : size+"vw"),
		height: (this.props.size === undefined ? "4vw" : size+"vw")
		}}>
	</button>
    );
  }
}

export default Button;
