import React, { Component } from 'react';
import ClefMenu from './ClefMenu.js'
import TimeMenu from './TimeMenu.js'
import './App.css'

class Popup extends Component {
  render() {
    return (
      <div className="popup">
	<div className="popup_inner">
	  <h2>{this.props.title}</h2>
	  <p style={{margin: "3vw"}}>{this.props.instructions}</p>
	  {this.props.type === "clef" ?
	  <ClefMenu callback={this.props.callback}/> :
	  this.props.type === "time" ?
          <TimeMenu callback={this.props.callback}/> :
	  this.props.type === "intro" ?
	  <button type="button" className="nbutton" style={{padding: "10px"}}
		onClick={this.props.callback}>I'm ready!</button> :
	  <p>wrong type</p>}
	</div>
      </div>
    );
  }
}

export default Popup;
