import React, { Component } from 'react';
import './App.css'

class Button extends Component {
  callback() {
    this.props.callback(this.props.image);
  }
  render() {
    return (
      <button type="button" className="nbutton" onClick={this.callback}
	style={{backgroundColor: (this.props.selected === this.props.image ? "skyblue" : "lightgrey"),
		backgroundImage: "url(" + require("./images/" + this.props.image + ".png") + ")"}}>
	</button>
    );
  }
}
 export default Button;
